{
  "id": "sf2e_job_001",
  "title": "The Kalo's Lost Cargo",
  "hook": "A frantic Kalo trader needs help recovering a shipment of rare xenoflora stolen by space goblins in the Diaspora.",
  "difficulty": 4,
  "reward": "500 credits per PC, a +1 weapon augmentation, and a favor from the Kalo Trading Guild.",
  "gm_notes": "The 'xenoflora' is actually a highly unstable, illegal bio-weapon prototype. The goblins don't know this, they just thought it looked shiny. A rival corporation (Veskarium Bio-Tech) is also tracking the cargo and might intervene.",
  "encounters": [
    "Space Goblin ambush on a derelict asteroid mining station.",
    "Navigating a debris field with environmental hazards (radiation pockets, unstable gravity).",
    "A tense negotiation/standoff with a Veskarium Bio-Tech retrieval team.",
    "Final confrontation with the Goblin Boss and their modified 'cargo' (a small mech powered by the unstable xenoflora)."
  ],
  "npcs": [
    {
      "name": "K'tharr",
      "role": "Kalo Trader",
      "description": "Anxious and overly dramatic, but genuinely desperate to get his cargo back without drawing attention."
    },
    {
      "name": "Snargle",
      "role": "Space Goblin Boss",
      "description": "A surprisingly cunning goblin who has jury-rigged the stolen cargo into a crude but dangerous weapon."
    },
    {
      "name": "Commander Zylos",
      "role": "Veskarium Bio-Tech Enforcer",
      "description": "Ruthless and efficient, will attempt to recover the cargo by any means necessary, prioritizing corporate assets over PC safety."
    }
  ]
}
